pragma solidity 0.8.20;
import "./CentralHub/MessageInterfaces.sol";
import "./KTokenInterfaces.sol";


contract KClient is KClientInterface {

    /*** User Interface ***/

    function mint(uint mintAmount) external payable override {
        bytes memory payload = abi.encode(
            KErc20CrossChainInterface.mint.selector,
            msg.sender,
            mintAmount
        );
        MessageInterface(centralHub).sendMessage{value: msg.value - mintAmount}(
            msg.sender,
            payload
        );
    }

    function repayBorrow(uint repayAmount) external payable override {
        bytes memory payload = abi.encode(
            KErc20CrossChainInterface.repayBorrow.selector,
            msg.sender,
            repayAmount
        );
        MessageInterface(centralHub).sendMessage{value: msg.value - repayAmount}(
            msg.sender,
            payload
        );
    }

    function repayBorrowBehalf(
        address borrower,
        uint repayAmount
    ) external payable override {
        bytes memory payload = abi.encode(
            KErc20CrossChainInterface.repayBorrowBehalf.selector,
            msg.sender,
            borrower,
            repayAmount
        );
        MessageInterface(centralHub).sendMessage{value: msg.value - repayAmount}(
            msg.sender,
            payload
        );
    }

    function liquidateBorrow(
        address borrower,
        uint repayAmount,
        KTokenInterface kTokenCollateral
    ) external payable override {
        bytes memory payload = abi.encode(
            KErc20CrossChainInterface.liquidateBorrow.selector,
            msg.sender,
            borrower,
            repayAmount,
            kTokenCollateral
        );
        MessageInterface(centralHub).sendMessage{value: msg.value - repayAmount}(
            msg.sender,
            payload
        );
    }


    /*** CentralHub Interface ***/

    function releaseETH(
        address payable recipient,
        uint amount
    ) external override returns (bool){
        require(msg.sender == centralHub, "Unauthorized");
        (bool success,) = recipient.call{value: amount}("");
        return success;
    }

    /*** Admin Functions ***/

    function _setCentralHub(address newCentralHub) external returns (bool){
        require(msg.sender == admin, "Unauthorized");

        address oldCentralHub = centralHub;
        centralHub = newCentralHub;

        emit NewCentralHub(
            oldCentralHub,
            centralHub
        );

        return true;
    }

    receive() external payable {}
}","keccak256":"0x326da475cdba1ac634d736cfd98b23dd8b906720f455f9d9be1e4e95ad071142","license":"AGPL-3.0"},"contracts/KClientDelegate.sol":{"content":"// SPDX-License-Identifier: BSD-3-Clause
pragma solidity 0.8.20;

import "./KTokenInterfaces.sol";
import "./KClient.sol";

/**
 * @title Kawa's KClientDelegate Contract
 * @notice KTokens which wrap an EIP-20 underlying and are delegated to
 * @author Kawa
 */
contract KClientDelegate is KClient, KDelegateInterface {
    /**
     * @notice Construct an empty delegate
     */
    constructor() {}

    /**
     * @notice Called by the delegator on a delegate to initialize it for duty
     * @param data The encoded bytes data for any initialization
     */
    function _becomeImplementation(bytes memory data) public virtual override {
        // Shh -- currently unused
        data;

        // Shh -- we don't ever want this hook to be marked pure
        if (false) {
            implementation = address(0);
        }

        require(
            msg.sender == admin,
            "only the admin may call _becomeImplementation"
        );
    }

    /**
     * @notice Called by the delegator on a delegate to forfeit its responsibility
     */
    function _resignImplementation() public virtual override {
        // Shh -- we don't ever want this hook to be marked pure
        if (false) {
            implementation = address(0);
        }

        require(
            msg.sender == admin,
            "only the admin may call _resignImplementation"
        );
    }
}
