// SPDX-License-Identifier: BSD-3-Clause
pragma solidity 0.8.20;

/**
 * @title Kawa's InterestRateModel Interface
 * @author Kawa
 */
abstract contract InterestRateModel {
    /// @notice Indicator that this is an InterestRateModel contract (for inspection)
    bool public constant isInterestRateModel = true;

    /**
     * @notice Calculates the current borrow interest rate per timestamp
     * @param cash The total amount of cash the market has
     * @param borrows The total amount of borrows the market has outstanding
     * @param reserves The total amount of reserves the market has
     * @return The borrow rate per timestamp (as a percentage, and scaled by 1e18)
     */
    function getBorrowRate(
        uint cash,
        uint borrows,
        uint reserves
    ) external view virtual returns (uint);

    /**
     * @notice Calculates the current supply interest rate per timestamp
     * @param cash The total amount of cash the market has
     * @param borrows The total amount of borrows the market has outstanding
     * @param reserves The total amount of reserves the market has
     * @param reserveFactorMantissa The current reserve factor the market has
     * @return The supply rate per timestamp (as a percentage, and scaled by 1e18)
     */
    function getSupplyRate(
        uint cash,
        uint borrows,
        uint reserves,
        uint reserveFactorMantissa
    ) external view virtual returns (uint);
}

contracts/KClient.sol.content:
// SPDX-License-Identifier: AGPL-3.0
pragma solidity 0.8.20;
import "./MessageHub/MessageHubInterfaces.sol";
import "./KTokenInterfaces.sol";

// todo check msg.value - amount for underflow
contract KClient is KClientInterface {

    /*** User Interface ***/

    function mint(uint mintAmount) external payable override {
        bytes memory payload = abi.encode(
            KErc20CrossChainInterface.mint.selector,
            msg.sender,
            mintAmount
        );
        MessageHubClientInterface(messageHub).sendMessage{value: msg.value - mintAmount}(
            msg.sender,
            payload,
            mintAmount
        );
    }

    function repayBorrow(uint repayAmount) external payable override {
        bytes memory payload = abi.encode(
            KErc20CrossChainInterface.repayBorrow.selector,
            msg.sender,
            repayAmount
        );
        MessageHubClientInterface(messageHub).sendMessage{value: msg.value - repayAmount}(
            msg.sender,
            payload,
            repayAmount
        );
    }

    function repayBorrowBehalf(
        address borrower,
        uint repayAmount
    ) external payable override {
        bytes memory payload = abi.encode(
            KErc20CrossChainInterface.repayBorrowBehalf.selector,
            msg.sender,
            repayAmount,
            borrower
        );
        MessageHubClientInterface(messageHub).sendMessage{value: msg.value - repayAmount}(
            msg.sender,
            payload,
            repayAmount
        );
    }

    function liquidateBorrow(
        address borrower,
        uint repayAmount,
        KTokenInterface kTokenCollateral
    ) external payable override {
        bytes memory payload = abi.encode(
            KErc20CrossChainInterface.liquidateBorrow.selector,
            msg.sender,
            borrower,
            repayAmount,
            kTokenCollateral
        );
        MessageHubClientInterface(messageHub).sendMessage{value: msg.value - repayAmount}(
            msg.sender,
            payload,
            repayAmount
        );
    }


    /*** MessageHub Interface ***/

    function releaseETH(
        address payable recipient,
        uint amount
    ) external override returns (bool){
        require(msg.sender == messageHub, "Unauthorized");
        (bool success,) = recipient.call{value: amount}("");
        return success;
    }

    /*** Admin Functions ***/

    function _setMessageHub(address newMessageHub) external {
        require(msg.sender == admin, "Unauthorized");

        address oldMessageHub = messageHub;
        messageHub = newMessageHub;

        emit NewMessageHub(
            oldMessageHub,
            messageHub
        );
    }

    receive() external payable {}
}

