// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.20;

import "./MessageHubBase.sol";

contract MessageHubClient is MessageHubBase, MessageHubClientInterface, AxelarExecutable {
    constructor(
        address kToken_,
        address gateway_,
        address gasReceiver_,
        address clientContract_,
        string memory clientChain_
    ) MessageHubBase(
    kToken_,
    gasReceiver_,
    clientContract_,
    clientChain_
    )
    AxelarExecutable(gateway_)
    {}

    function sendMessage(
        address sender,
        bytes memory payload,
        uint amount
    ) external payable override {
        require(msg.sender == kToken, "Unauthorized");

        uint gas = gasReceiver.estimateGasFee(
            clientChain,
            clientContract,
            payload,
            2000,
            ""
        );
        uint ethUsed = gas + amount;
        require(msg.value >= ethUsed, "Insufficient funds");

        gasReceiver.payNativeGasForContractCall{value: gas}(
            address(this),
            clientChain,
            clientContract,
            payload,
            sender
        );

        gateway().callContract(clientChain, clientContract, payload);
        _sendEth(payable(sender), msg.value - ethUsed);
    }

    function _execute(
        bytes32 commandId,
        string calldata sourceChain,
        string calldata sourceAddress,
        bytes calldata payload
    ) internal override {
//        require(msg.sender == address(gateway), "Only Axelar Gateway can call this");
//        require(clientContract == sourceAddress.toAddress(), "Only Source Contract can call this");

        bytes32 payloadHash = keccak256(payload);

//        require(
//            gateway.validateContractCall(
//                commandId,
//                sourceChain,
//                sourceAddress,
//                payloadHash
//            ),
//            "Validation Error"
//        );

        (address recipient, uint amount) = abi.decode(payload, (address, uint));
        KClientInterface(kToken).releaseETH(payable(recipient), amount);
    }

}

